    static class Puzzle1 {

        public static void main(String[] args) {
            int level = 0;
            for (char c : INPUT.toCharArray()) {
                if (c == '(') {
                    level++;
                } else if (c == ')') {
                    level--;
                }
            }
            System.out.println(level);
        }

    }

    static class Puzzle2 {

        public static void main(String[] args) {
            int level = 0;
            int position = 1;
            for (char c : INPUT.toCharArray()) {
                if (c == '(') {
                    level++;
                } else if (c == ')') {
                    level--;
                }
                if (level < 0) {
                    System.out.println(position);
                    return;
                }
                position++;
            }
        }

    }

